css.set("width", $box.w + "px");

collection.setCssTextAlignment(css, $textAlign.h, $textAlign.v);
collection.setCssFontWeight(css, $weight);

specs.push({
	_name: "div",
	_uri: "http://www.w3.org/1999/xhtml",
	_text: text,
	style: css
});

Dom.newDOMFragment(specs);

